class Smc_Config {
	private $name;
	private $value;
	private $is_good;

	public function __construct( $name, $value, $is_good ) {
		$this->name    = $name;
		$this->value   = $value;
		$this->is_good = $is_good;
	}

	public function html() {
		Ai1wm_Template::render(
			'config-item',
			array(
				'name'    => $this->name,
				'value'   => $this->value,
				'is_good' => $this->is_good,
			),
			SMC_TEMPLATES_PATH
		);
	}
}
